export default {
  name: "SortButtons",
  props: ["movies"],

  data() {
    return {
      sortedByDate: false,
      sortedByRating: false,
      sortedByTitle: false
    };
  },

  methods: {
    sortByDate() {
      const tempMovies = this.movies;

      if (!this.sortedByDate) {
        tempMovies.sort((a, b) => {
          if (a.release_date > b.release_date) {
            return 1;
          }

          if (a.release_date < b.release_date) {
            return -1;
          }

          return 0;
        });
        this.sortedByDate = true;
      } else {
        tempMovies.sort((a, b) => {
          if (a.release_date < b.release_date) {
            return 1;
          }

          if (a.release_date > b.release_date) {
            return -1;
          }

          return 0;
        });
        this.sortedByDate = false;
      }
    },

    sortByRank() {
      const tempMovies = this.movies;

      if (!this.sortedByRating) {
        tempMovies.sort((a, b) => {
          if (a.vote_average > b.vote_average) {
            return 1;
          }

          if (a.vote_average < b.vote_average) {
            return -1;
          }

          return 0;
        });
        this.sortedByRating = true;
      } else {
        tempMovies.sort((a, b) => {
          if (a.vote_average < b.vote_average) {
            return 1;
          }

          if (a.vote_average > b.vote_average) {
            return -1;
          }

          return 0;
        });
        this.sortedByRating = false;
      }
    },

    // Sort 
    sortByTitle() {
      const tempMovies = this.movies;

      if (!this.sortedByTitle) {
        tempMovies.sort((a, b) => {
          if (a.title > b.title) {
            return 1;
          }

          if (a.title < b.title) {
            return -1;
          }

          return 0;
        });
        this.sortedByTitle = true;
      } else {
        tempMovies.sort((a, b) => {
          if (a.title > b.title) {
            return 1;
          }

          if (a.title < b.title) {
            return -1;
          }

          return 0;
        });
        this.sortedByTitle = false;
      }
    }

  }
};
