import axios from "axios";
import MoviesList from "../card/MoviesList.vue";
export default {
  name: "AmericanMovies",
  components: {
    MoviesList
  },

  data() {
    return {
      AmericanMovies: [],
      preUrl: "https://image.tmdb.org/t/p/original/"
    };
  },

  created: function () {
    axios.get("https://api.themoviedb.org/3/discover/movie?api_key=f59ed1baf836b3d5166c1566153e85ac&language=fr&sort_by=popularity.desc&include_adult=false&include_video=false&page=1&certification_country=us&with_original_language=en&page1").then(res => {
      this.AmericanMovies = res.data.results;
      axios.get("https://api.themoviedb.org/3/discover/movie?api_key=f59ed1baf836b3d5166c1566153e85ac&language=fr&sort_by=popularity.desc&include_adult=false&include_video=false&page=1&certification_country=us&with_original_language=en&page2").then(response => {
        let AmericanTempMovies = this.AmericanMovies;
        response.data.results.forEach(function (movie) {
          AmericanTempMovies.push(movie);
        });
        this.AmericanMovies = AmericanTempMovies;
        console.log(this.AmericanMovies);
      });
    });
  }
};
